CREATE TABLE IF NOT EXISTS "tm_tool" (
    "id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "tool_code" varchar NOT NULL,
    "tool_name" varchar NOT NULL,
    "brand" varchar,
    "spec" varchar,
    "tool_type_id" bigint NOT NULL,
    "tool_type_code" varchar,
    "tool_type_name" varchar,
    "code_flag" varchar NOT NULL,
    "quantity" int NOT NULL,
    "quantity_avail" int,
    "mainten_type" varchar,
    "next_mainten_period" int,
    "next_mainten_date" varchar,
    "status" varchar,
    "remark" varchar,
    "attr1" varchar,
    "attr2" varchar,
    "attr3" int,
    "attr4" int,
    "creator" varchar DEFAULT '',
    "create_time" datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
    "updater" varchar DEFAULT '',
    "update_time" datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    "deleted" bit NOT NULL DEFAULT FALSE,
    "tenant_id" bigint NOT NULL,
    PRIMARY KEY ("id")
) COMMENT '工装夹具清单表';

